  })(jQuery);


  function contentRevealOnClick(clickedButton) {
    var revealedContent = clickedButton.closest('.single-content-block').find('.js-hidden-full-content');
    var revealedContentInner = clickedButton.closest('.single-content-block').find('.js-hidden-full-content-inner').height();
    clickedButton.prop('disabled', true);
    $(revealedContent).css('max-height', revealedContentInner);
    $(revealedContent).toggleClass('active');
    $(revealedContent).delay(2000).queue(function (next) { $(revealedContent).css('max-height', $(revealedContent).height()); next(); $('.js-show-more').prop('disabled', false); });
    clickedButton.toggleClass('open');
  }

  function afterScrollShowCorrectAncour(openBlock) {
    $('.sidebar-option-inner').removeClass('active');
    $('.js-sidebar-option[data-anchor="' + openBlock + '"]').find('.sidebar-option-inner').addClass('active');
  }

</script>