<script type="text/javascript">
    $('.js-sendto-google form').voogAjaxForm({
        success: function() {
            //Add styles to completed form notice
            if ($('.js-contact-main-form').length !== -1) {
                $('.form_error').closest($('.js-show-form')).addClass('hidden');
                $('.form_error').closest('.js-contact-main-form').addClass('active');
                $('.form_notice').closest($('.js-show-form')).addClass('hidden');
                $('.form_notice').closest('.js-contact-main-form').addClass('active');
            }
            $('.form_notice').closest('body').find('.contact-main-button-wrap').addClass('hidden');
            $('.form_error').closest('body').find('.contact-main-button-wrap').addClass('hidden');

            // Google forms post
            var formType = 'Eriprojektid';
            var name = '';
            var email = '';
            var comments = '';
            var phone = '';
            var houseModel = '';
            var houseHTML = '';
            var mailSubject = '';
            var buildingTime = '';
            var queryResponseType = '';


            if (modelName === true || customProject === true) {
                houseModel = '';
                if (!houseModel) {
                    houseModel = 'Special projects';
                }
            }

            var sentFormNotice = $('.js-details-form').find('.form_notice');
            var passedForm = $(sentFormNotice).closest('.js-sendto-google form');

            name = $(passedForm).find('.js-form-name > input').val();
            email = $(passedForm).find('.js-form-email > input').val();
            comments = $(passedForm).find('.js-form-comments > input').val();
            phone = $(passedForm).find('.js-form-phone > input').val();
            buildingTime = $(passedForm).find('.js-form-build-time > input').val();
            queryResponseType = $(passedForm).find('.js-form-response-type select').val();

            // Variables not used in google form. For the mail.
            houseHTML = $('.js-mail-innercontent').html();
            mailSubject = $('.js-mail-subject').data('mailsubject');
            mailFilesString = $('.js-house-mailfiles').data('mailfilesarray');


            var sentForm = $('.js-details-form').find('.form_notice').closest('form');
            $(sentForm).find('.form_fields').addClass('hidden');
            $(sentForm).find('.form_submit').addClass('hidden');
            $(sentFormNotice).empty().append('<div class="form-success-icon"></div><div class="form-success-txt"><span>Message sent</span>You will be contacted shortly</div>');
            sendGoogle(formType, email, name, comments, phone, houseModel, buildingTime, queryResponseType);

            if ('2041597' === '2146461' || $('.js-autoreply').data('autoreply') === true) {
                sendEmail({ email: email, housetypehtml: houseHTML, mailSubject: mailSubject, mailFilesString: mailFilesString});
            }
        },
        error: function(text) {
/*
            if ($('.js-form-time').hasClass('form_field_with_errors')) {
                $('<div class="form_field_error"></div>').appendTo($('.timepicker-wrapper'));
                $('.timepicker-wrapper').addClass('form_field_with_errors');
            }
*/
        },
        formdata_error: "Your browser is too old to support file upload from this form."
    });

        function sendGoogle(formType, email, name, comments, phone, houseModel, time, responseType){
            $.ajax({
                url: "https://docs.google.com/forms/d/e/1FAIpQLSfc_foUV0ISuZpuj026yQfvYUO7DA1zlg4OC7ktion6slgraA/formResponse",
                data: {
                    "entry.513745673": houseModel,
                    "entry.561378803": formType,
                    "entry.1731183234": name,
                    "entry.167171795": email,
                    "entry.893916540": comments,
                    "entry.773820525": phone,
                    "entry.815644291": time,
                    "entry.556139264": responseType
                },
                type: "POST",
                dataType: 'xml',
                statusCode: {
                    0: function (response){
                        //console.log('0');
                    },
                    200: function (response){
                        //console.log('200');
                    }
                }
            });
        }

        function sendEmail(data) {
            $.ajax({
                url: "https://mailapi.houses.ee/",
                data: data,
                method: "POST",
                success: function (response) {
                    console.log(response);
                },
                error: function (error) {
                    console.log(error);
                }
            });
        }
</script>



    <script type="text/javascript">


        //Form animations
        formAnimations();

        var $formLabel = $('.details-form-upload').find('label');
        var $formInput = $('.details-form-upload').find('input');
        $formInput.on('change', function () {
            $formLabel.empty().text($formInput.val().replace(/^.*\\/, ""));
            if ($formInput.val() !== '' && !$('.details-form-upload').hasClass('active')) {
                $('.details-form-upload').addClass('active');
            }else{
                $('.details-form-upload').removeClass('active');
                $formLabel.text('Choose file');
            }
        });

        //JS for generating the processtree
        var processTree = $.trim($('.js-custom-project-process').text()).split(">");
        $.each(processTree, function(i, processItem) {
            $('.js-sidebar-desk-process').append('<div class="sidebar-process-item">'+ processItem +'<span class="sidebar-process-item-icon"></span></div>');
            $('.js-sidebar-mob-process').append('<div class="sidebar-process-item">'+ processItem +'<span class="sidebar-process-item-icon"></span></div>');
        });

        //Give processsidebar height same as content
        var $sidebarHeight = $('.js-right-content').height() + $('.main-content-inner-single').height();
        $('.js-sidebar-process-desk').css('height' , $sidebarHeight);
        var $innerSidebarHeight = $sidebarHeight - $('.sidebar-process-wrap-heading').outerHeight(true) - 80; /*15px padding + extra 65px for more space*/
        $('.js-sidebar-process-desk .sidebar-process').height($innerSidebarHeight);
        var $desktopItem = $('.js-sidebar-process-desk .sidebar-process-item');
        var $itemsHeight = $innerSidebarHeight / ($desktopItem.length - 1); /* The last item does not have height */
        $desktopItem.each(function(i, item){
            $(item).height($itemsHeight);
        });

        $desktopItem.last().css('height', 'auto');

    </script>

